import React from "react";
import { useState, useEffect } from "react";
import "./App.css";
import mondaySdk from "monday-sdk-js";
import "monday-ui-react-core/dist/main.css";
//Explore more Monday React Components here: https://style.monday.com/
import { Dropdown, Button, AttentionBox } from "monday-ui-react-core";

// Usage of mondaySDK example, for more information visit here: https://developer.monday.com/apps/docs/introduction-to-the-sdk/
const monday = mondaySdk();

const Popup = () => {
  const [showSelectUserError, setShowSelectUserError] = useState(false);
  return (
    showSelectUserError &&
      <AttentionBox
        className="monday-storybook-attention-box_box"
        onClose={function noRefCheck(){}}
        text="Studies show that 100% of people who celebrate birthdays, will eventually die."
        title="Attention box title"
      />
  );
}

const App = () => {
  const [context, setContext] = useState();
  const [allOptions, setOptions] = useState([]);
  const [selectedAttorney, setSelectedAttorney] = useState("");
  const [selectedParalegal, setSelectedParalegal] = useState("");

  //context.user.id
  //Some example what you can do with context, read more here: https://developer.monday.com/apps/docs/mondayget#requesting-context-and-settings-data  

  useEffect(() => {
    // Notice this method notifies the monday platform that user gains a first value in an app.
    // Read more about it here: https://developer.monday.com/apps/docs/mondayexecute#value-created-for-user/
    //monday.execute("valueCreatedForUser");

    // TODO: set up event listeners, Here`s an example, read more here: https://developer.monday.com/apps/docs/mondaylisten/
    monday.listen("context", (res) => {
      console.log(res.data);
      setContext(res.data);
    });
  }, []);

  function createSubtasks(){
    if (selectedAttorney && selectedParalegal)
      alert(`${selectedAttorney} and ${selectedParalegal}`);
  }

  function attorneyChanged(item){
    setSelectedAttorney(item.value);
  }

  function paralegalChanged(item){
    setSelectedParalegal(item.value);
  }

  monday.api(`query { users { id, name } }`).then(res => {
    /* { data: { users: [{id: 12312, name: "Bart Simpson"}, {id: 423423, name: "Homer Simpson"}] } } */
    let newOptions = [];
    for (let user of res.data.users){
      newOptions.push({value: user.name, label: user.name});
    }
    setOptions(newOptions);
  });

  return (
    <div className="App">
      <div>
        <Dropdown onChange={attorneyChanged} placeholder="Please Select an Attorney" options={allOptions} className="dropdown-select-member" />
      </div>
      <div>
        <Dropdown onChange={paralegalChanged} placeholder="Please Select an Paralegal" options={allOptions} className="dropdown-select-member" />
      </div>      
      <div>
        <Button className="create-subtasks" onClick={createSubtasks}>Generate Tasks</Button>
      </div>
      <Popup></Popup>
    </div>
  );
};

export default App;
